import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [companies, setCompanies] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const api = axios.create({
      baseURL: "http://127.0.0.1:8000",
    });
    api
      .get("/companies/")
      .then((response) => {
        setCompanies(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  useEffect(() => {
    const filteredCompanies = companies.filter((company) => {
      // Search by company name and reg_code
      const companyMatch =
        company.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        company.reg_code.includes(searchTerm);

      // Search by members' name and reg_code/personal_code
      const memberMatch = company.members.some((member) => {
        return (
          member.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          (member.reg_code && member.reg_code.includes(searchTerm)) ||
          (member.personal_code && member.personal_code.includes(searchTerm))
        );
      });

      return companyMatch || memberMatch;
    });

    setSearchResults(filteredCompanies);
  }, [searchTerm, companies]);

  return (
    <div className="App">
      <h1>Company List</h1>
      <input
        type="text"
        placeholder="Search by name, registration code, or member's name/reg_code/personal_code"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <ul>
        {searchResults.map((company) => (
          <li key={company.id}>
            {company.name} - {company.reg_code}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
