import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [companies, setCompanies] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const api = axios.create({
      baseURL: "http://127.0.0.1:8000",
    });
    api
      .get("/companies/")
      .then((response) => {
        setCompanies(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  useEffect(() => {
    const filteredResults = companies.filter((company) => {
      const isCompanyMatch =
        company.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        company.reg_code.includes(searchTerm);

      return (
        isCompanyMatch ||
        company.members.some((member) => {
          const isPersonMatch =
            member.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            member.personal_code.includes(searchTerm);
          return isPersonMatch;
        })
      );
    });

    setSearchResults(filteredResults);
  }, [searchTerm, companies]);

  return (
    <div className="App">
      <h1>Company List</h1>
      <input
        type="text"
        placeholder="Search by name, registration code, member name, or personal code"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <ul>
        {searchResults.map((result) => (
          <li key={result.id}>
            {result.isCompany
              ? `${result.name} - ${result.reg_code}`
              : `${result.name} - ${result.personal_code}`}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
