import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import CompanySearch from "../CompanySearch";
import { useNavigate } from "react-router-dom";
import AddMemberModal from "../AddMemberModal";

function CreateCompany() {
  const navigate = useNavigate();
  const formatCurrentDate = () => {
    const currentDate = new Date();

    const year = currentDate.getFullYear();
    const month = (currentDate.getMonth() + 1).toString().padStart(2, "0"); // Months are zero-based
    const day = currentDate.getDate().toString().padStart(2, "0");

    return `${year}-${month}-${day}`;
  };

  const [formData, setFormData] = useState({
    name: "",
    regCode: "",
    createdAt: formatCurrentDate(),
    capital: 0,
    members: [],
  });

  const [people, setPeople] = useState([]);
  const [personSearchTerm, setPersonSearchTerm] = useState("");
  const [message, setMessage] = useState("");
  const [searchResults, setSearchResults] = useState(people);

  const [member, setMember] = useState();
  const [memberType, setMemberType] = useState("person");
  const [capital, setCapital] = useState(0);
  const [role, setRole] = useState("founder");

  const modalRef = useRef();

  useEffect(() => {
    closeModal();
    const api = axios.create({
      baseURL: "http://127.0.0.1:8000",
    });
    api
      .get("/people")
      .then((response) => {
        setPeople(response.data);
        setSearchResults(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const checkCapital = () => {
    let totalMembersCapital = 0;
    formData.members.map((member) => {
      console.log(member);
      totalMembersCapital += parseInt(member.capital);
    });
    if (totalMembersCapital !== parseInt(formData.capital)) {
      setMessage("Company capital is not equal to the sum of members capital.");
      return false;
    }
    return true;
  };

  const handleSearch = (event) => {
    const query = event.target.value;
    setPersonSearchTerm(query);

    const filteredResults = people.filter(
      (person) =>
        person.name.toLowerCase().includes(query.toLowerCase()) ||
        person.personal_code.includes(query)
    );

    setSearchResults(filteredResults);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (checkCapital()) {
      try {
        await axios.post("http://127.0.0.1:8000/company", formData);
        setMessage("Company created successfully.");
        navigate(`/company/${formData.regCode}`);
      } catch (error) {
        error.response.data.detail
          ? setMessage(error.response.data.detail)
          : setMessage("Error creating new company");
      }
    }
  };

  const handlePersonClick = (person) => {
    setMember(person);
    setMemberType("person");
    openModal();
  };

  const handleCompanyClick = (company) => {
    setMember(company);
    setMemberType("company");
    openModal();
  };

  const openModal = () => {
    modalRef.current.style.display = "block";
  };

  const closeModal = () => {
    modalRef.current.style.display = "none";
  };

  const handleModalSubmit = () => {
    formData.members.push({
      name: member.name,
      id: member.id,
      role: role,
      capital: capital,
      is_person: memberType === "person",
    });

    closeModal();
  };

  return (
    <div>
      <h2>Create New Company</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="regCode">Registration Code:</label>
          <input
            type="text"
            id="regCode"
            name="regCode"
            value={formData.regCode}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="capital">Capital:</label>
          <input
            type="number"
            min={2500}
            id="capital"
            name="capital"
            value={formData.capital}
            onChange={handleChange}
          />
        </div>

        <p>Members:</p>
        {formData.members.map((member) => (
          <p>{member.name}</p>
        ))}

        <h1>Person List</h1>
        <div>
          <input
            type="text"
            placeholder="Search by name or personal code"
            value={personSearchTerm}
            onChange={handleSearch}
          />
          <ul>
            {searchResults.map((person) => (
              <li key={person.id} onClick={() => handlePersonClick(person)}>
                {person.name} - Personal Code: {person.personal_code}
              </li>
            ))}
          </ul>
        </div>

        <button type="submit">Create Company</button>
      </form>
      <p>{message}</p>

      <CompanySearch handleCompanyClick={handleCompanyClick} />
      <AddMemberModal
        closeModal={closeModal}
        member={member}
        capital={capital}
        setCapital={setCapital}
        setRole={setRole}
        role={role}
        handleModalSubmit={handleModalSubmit}
      />

      <div ref={modalRef}>
        <span className="close" onClick={closeModal}>
          &times;
        </span>
        <h3>Assign Role and Capital</h3>
        {member && <h2>{member.name}</h2>}
        <label>
          Capital:
          <input
            type="number"
            value={capital}
            onChange={(e) => setCapital(e.target.value)}
          />
        </label>
        <label>
          Role:
          <select value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="founder">Founder</option>
            <option value="member">Member</option>
          </select>
        </label>
        <button onClick={handleModalSubmit}>Add Person to Company</button>
      </div>
    </div>
  );
}

export default CreateCompany;
