import React, { useState, useEffect } from "react";
import axios from "axios";

function CreateCompany() {
  const [formData, setFormData] = useState({
    name: "",
    regCode: "",
    createdAt: "2001-05-20",
    capital: "",
    members: [],
  });

  const [people, setPeople] = useState([]);
  const [personSearchTerm, setPersonSearchTerm] = useState("");
  const [message, setMessage] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const api = axios.create({
      baseURL: "http://127.0.0.1:8000",
    });
    api
      .get("/people")
      .then((response) => {
        setPeople(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  useEffect(() => {
    const filteredCompanies = companies.filter((company) => {
      const companyMatch =
        searchByCompany &&
        (company.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          company.reg_code.includes(searchTerm));

      const memberMatch =
        searchByMembers &&
        company.members.some((member) => {
          return (
            member.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            (member.reg_code && member.reg_code.includes(searchTerm)) ||
            (member.personal_code && member.personal_code.includes(searchTerm))
          );
        });

      return companyMatch || memberMatch;
    });

    setSearchResults(filteredCompanies);
  }, [personSearchTerm]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post("http://127.0.0.1:8000/company", formData);
      setMessage("Company created successfully.");
      setFormData({
        name: "",
        regCode: "",
        createdAt: "",
      });
    } catch (error) {
      setMessage("Error creating the company.");
      console.error("Error:", error);
    }
  };

  return (
    <div>
      <h2>Create New Company</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="regCode">Registration Code:</label>
          <input
            type="text"
            id="regCode"
            name="regCode"
            value={formData.regCode}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="capital">Created At:</label>
          <input
            type="numer"
            min={2500}
            id="capital"
            name="capital"
            value={formData.capital}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Create Company</button>
      </form>
      <p>{message}</p>
    </div>
  );
}

export default CreateCompany;
